$(document).ready(function(){const n=$("#about");let t="";$.ajax({url:"../content/about-content.json",method:"GET"}).then(o=>{o.about.forEach(n=>{t+=`\n            <h1>${n.pic} ${n.name}</h1>\n                <p>${n.background}</p>\n                `}),n.html(t)})});